<?php

require_once __DIR__ . '/Solution.php';

$dataProvider = [
    'a' => 'a',
    'babad' => 'aba',
    'cbbd' => 'bb',
    'qwerewq' => 'qwerewq',
    'qweewq' => 'qweewq',
    '' => '',
];

foreach ($dataProvider as $value => $expected) {
    $solution = new Solution();
    $got = $solution->longestPalindrome($value);
    if ($got !== $expected) {
        printf("Expected %s got %s for string %s\n", $expected, $got, $value);
    }
}

